import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import './App.css';
import './index.css';
import './styles/Dashboard.css';

// Components
import Header from './components/Layout/Header';
import Footer from './components/Layout/Footer';
import HomePage from './pages/Home';
import CompatibilityChecker from './components/CompatibilityChecker';
import RegionalDistribution from './components/RegionalDistribution';
import LoginForm from './components/Auth/LoginForm';
import RegisterForm from './components/Auth/RegisterForm';
import { AuthProvider } from './contexts/AuthContext';

// Conditional Header component
const ConditionalHeader = () => {
  const location = useLocation();
  // Only show header on non-home routes
  return location.pathname !== '/' ? <Header /> : null;
};

// Main content with routes
const MainContent = () => {
  const location = useLocation();
  const isHomePage = location.pathname === '/';
  
  return (
    <>
      <ConditionalHeader />
      <main className={`main-content ${isHomePage ? 'home-page' : ''}`}>
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/compatibility" element={<CompatibilityChecker />} />
          <Route path="/regional-data" element={<RegionalDistribution />} />
          <Route path="/login" element={<LoginForm />} />
          <Route path="/register" element={<RegisterForm />} />
          <Route path="*" element={
            <div style={{ padding: '2rem', textAlign: 'center' }}>
              <h2>Page Not Found</h2>
              <p>The page you're looking for doesn't exist or has been moved.</p>
            </div>
          } />
        </Routes>
      </main>
    </>
  );
};

const App: React.FC = () => {
  return (
    <AuthProvider>
      <Router>
        <div className="app-container">
          <MainContent />
          <Footer />
          <Toaster position="bottom-right" />
        </div>
      </Router>
    </AuthProvider>
  );
};

export default App;
