import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const Header: React.FC = () => {
  const { user, signOut } = useAuth();
  const navigate = useNavigate();

  const handleSignOut = async () => {
    await signOut();
    navigate('/');
  };

  return (
    <header className="App-header">
      <div className="header-content">
        <div className="logo">
          <Link to="/">
            <h1>Blood Donation Awareness</h1>
          </Link>
        </div>
        <nav className="main-nav">
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/compatibility">Compatibility Checker</Link>
            </li>
            <li>
              <Link to="/regional-data">Regional Data</Link>
            </li>
          </ul>
        </nav>
        <div className="auth-nav">
          {user ? (
            <div className="user-menu">
              <Link to="/dashboard" className="dashboard-btn">Dashboard</Link>
              <button onClick={handleSignOut} className="logout-btn">Logout</button>
            </div>
          ) : (
            <div className="auth-buttons">
              <Link to="/login" className="login-btn">Login</Link>
              <Link to="/register" className="register-btn">Register</Link>
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
