import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { Session, User } from '@supabase/supabase-js';
import { supabase, UserProfile, getUserProfile, createUserProfile } from '../services/supabaseClient';

// Define the shape of our auth context
interface AuthContextType {
  session: Session | null;
  user: User | null;
  profile: UserProfile | null;
  loading: boolean;
  error: string | null;
  signUp: (email: string, password: string) => Promise<{ error: any }>;
  signIn: (email: string, password: string) => Promise<{ error: any }>;
  signOut: () => Promise<void>;
  resetPassword: (email: string) => Promise<{ error: any }>;
  updateProfile: (updates: Partial<UserProfile>) => Promise<{ error: any }>;
}

// Create the context with a default undefined value
const AuthContext = createContext<AuthContextType | undefined>(undefined);

// Props for the AuthProvider component
interface AuthProviderProps {
  children: ReactNode;
}

// AuthProvider component
export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [session, setSession] = useState<Session | null>(null);
  const [user, setUser] = useState<User | null>(null);
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Initialize the auth state
  useEffect(() => {
    setLoading(true);
    console.log('AuthProvider: Initializing auth state...');

    // Get the current session
    const fetchSession = async () => {
      try {
        console.log('AuthProvider: Fetching session...');
        const { data, error } = await supabase.auth.getSession();
        
        if (error) {
          console.error('AuthProvider: Error getting session:', error);
          throw error;
        }

        console.log('AuthProvider: Session data:', data?.session ? 'Session exists' : 'No session');
        setSession(data.session);
        setUser(data.session?.user || null);

        // Fetch user profile if user exists
        if (data.session?.user) {
          console.log('AuthProvider: User exists, fetching profile...');
          const { data: profileData, error: profileError } = await getUserProfile(data.session.user.id);
          
          if (profileError) {
            console.error('AuthProvider: Error fetching user profile:', profileError);
          } else {
            console.log('AuthProvider: Profile fetched successfully');
            setProfile(profileData);
          }
        }
      } catch (error: any) {
        console.error('AuthProvider: Error initializing auth:', error.message);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchSession();

    // Set up a listener for auth state changes
    const { data: authListener } = supabase.auth.onAuthStateChange(async (event, newSession) => {
      console.log('AuthProvider: Auth state changed:', event);
      setSession(newSession);
      setUser(newSession?.user || null);
      setLoading(true);

      if (event === 'SIGNED_IN' && newSession?.user) {
        console.log('AuthProvider: User signed in, checking profile...');
        // Check if profile exists
        const { data, error: profileError } = await getUserProfile(newSession.user.id);
        
        if (profileError || !data) {
          console.log('AuthProvider: Profile not found, creating new profile...');
          // Create new profile if it doesn't exist
          const { data: newProfile, error: createError } = await createUserProfile({
            id: newSession.user.id,
            blood_type: null,
            region: null,
            donation_count: 0,
            last_donation_date: null,
          });
          
          if (createError) {
            console.error('AuthProvider: Error creating user profile:', createError);
          } else {
            console.log('AuthProvider: New profile created successfully');
            setProfile(newProfile);
          }
        } else {
          console.log('AuthProvider: Existing profile found');
          setProfile(data);
        }
      } else if (event === 'SIGNED_OUT') {
        console.log('AuthProvider: User signed out');
        setProfile(null);
      }
      
      setLoading(false);
    });

    // Clean up subscription on unmount
    return () => {
      console.log('AuthProvider: Cleaning up auth listener');
      authListener?.subscription.unsubscribe();
    };
  }, []);

  // Sign up with email and password
  const signUp = async (email: string, password: string) => {
    try {
      console.log('AuthProvider: Signing up user:', email);
      setLoading(true);
      
      const { data, error } = await supabase.auth.signUp({ 
        email, 
        password,
        options: {
          // This will skip email verification
          data: {
            email_confirmed: true
          }
        }
      });
      
      if (error) {
        console.error('AuthProvider: Sign up error:', error);
      } else {
        console.log('AuthProvider: Sign up successful, user created:', data?.user?.id);
        
        // Auto sign in after signup
        console.log('AuthProvider: Auto signing in after signup...');
        await supabase.auth.signInWithPassword({ email, password });
      }
      
      return { error };
    } catch (error: any) {
      console.error('AuthProvider: Exception during sign up:', error.message);
      return { error };
    } finally {
      setLoading(false);
    }
  };

  // Sign in with email and password
  const signIn = async (email: string, password: string) => {
    try {
      console.log('AuthProvider: Signing in user:', email);
      setLoading(true);
      
      const { data, error } = await supabase.auth.signInWithPassword({ email, password });
      
      if (error) {
        console.error('AuthProvider: Sign in error:', error);
      } else {
        console.log('AuthProvider: Sign in successful, user:', data?.user?.id);
      }
      
      return { error };
    } catch (error: any) {
      console.error('AuthProvider: Exception during sign in:', error.message);
      return { error };
    } finally {
      setLoading(false);
    }
  };

  // Sign out
  const signOut = async () => {
    try {
      setLoading(true);
      await supabase.auth.signOut();
      setProfile(null);
    } catch (error: any) {
      console.error('Error signing out:', error.message);
    } finally {
      setLoading(false);
    }
  };

  // Reset password
  const resetPassword = async (email: string) => {
    try {
      setLoading(true);
      const { error } = await supabase.auth.resetPasswordForEmail(email);
      return { error };
    } catch (error: any) {
      console.error('Error resetting password:', error.message);
      return { error };
    } finally {
      setLoading(false);
    }
  };

  // Update user profile
  const updateProfile = async (updates: Partial<UserProfile>) => {
    try {
      setLoading(true);
      if (!user) {
        return { error: new Error('No user logged in') };
      }

      const { data, error } = await supabase
        .from('user_profiles')
        .update(updates)
        .eq('id', user.id)
        .select()
        .single();

      if (error) {
        throw error;
      }

      setProfile(data);
      return { error: null };
    } catch (error: any) {
      console.error('Error updating profile:', error.message);
      return { error };
    } finally {
      setLoading(false);
    }
  };

  // Provide the auth context value
  const value = {
    session,
    user,
    profile,
    loading,
    error,
    signUp,
    signIn,
    signOut,
    resetPassword,
    updateProfile,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Custom hook to use the auth context
export const useAuth = () => {
  const context = useContext(AuthContext);
  
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  
  return context;
};
