import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

const Header: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // Handle scroll effect for transparent to solid header transition
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleSignOut = () => {
    // Simplified sign out without AuthContext
    navigate('/');
    setMobileMenuOpen(false);
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setMobileMenuOpen(false);
  };

  // Check if the current path matches the link
  const isActive = (path: string) => {
    return location.pathname === path;
  };

  return (
    <header className={`modern-header inner-page-header ${scrolled ? 'scrolled' : ''}`}>
      <div className="header-content">
        <div className="logo">
          <Link to="/" onClick={closeMobileMenu}>
            <h1>Blood Donation Awareness</h1>
          </Link>
        </div>

        <button 
          className={`mobile-menu-toggle ${mobileMenuOpen ? 'active' : ''}`} 
          onClick={toggleMobileMenu}
          aria-label="Toggle menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </button>

        <div className={`nav-container ${mobileMenuOpen ? 'open' : ''}`}>
          <nav className="main-nav">
            <ul>
              <li>
                <Link 
                  to="/" 
                  className={isActive('/') ? 'active' : ''} 
                  onClick={closeMobileMenu}
                >
                  Home
                </Link>
              </li>
              <li>
                <Link 
                  to="/compatibility" 
                  className={isActive('/compatibility') ? 'active' : ''} 
                  onClick={closeMobileMenu}
                >
                  Compatibility
                </Link>
              </li>
              <li>
                <Link 
                  to="/regional-data" 
                  className={isActive('/regional-data') ? 'active' : ''} 
                  onClick={closeMobileMenu}
                >
                  Regional Data
                </Link>
              </li>
            </ul>
          </nav>
          
          <div className="auth-nav">
            <Link 
              to="/login" 
              className={`login-btn ${isActive('/login') ? 'active' : ''}`}
              onClick={closeMobileMenu}
            >
              Login
            </Link>
            <Link 
              to="/register" 
              className="get-started-btn"
              onClick={closeMobileMenu}
            >
              Get Started
            </Link>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
