import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const Header: React.FC = () => {
  const { user, signOut } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const handleSignOut = async () => {
    await signOut();
    navigate('/');
    setMobileMenuOpen(false);
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setMobileMenuOpen(false);
  };

  // Check if the current path matches the link
  const isActive = (path: string) => {
    return location.pathname === path;
  };

  return (
    <header className="App-header">
      <div className="header-content">
        <div className="logo">
          <Link to="/" onClick={closeMobileMenu}>
            <h1>Blood Donation Awareness</h1>
          </Link>
        </div>

        <button 
          className={`mobile-menu-toggle ${mobileMenuOpen ? 'active' : ''}`} 
          onClick={toggleMobileMenu}
          aria-label="Toggle menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </button>

        <div className={`nav-container ${mobileMenuOpen ? 'open' : ''}`}>
          <nav className="main-nav">
            <ul>
              <li>
                <Link 
                  to="/" 
                  className={isActive('/') ? 'active' : ''} 
                  onClick={closeMobileMenu}
                >
                  Home
                </Link>
              </li>
              <li>
                <Link 
                  to="/compatibility" 
                  className={isActive('/compatibility') ? 'active' : ''} 
                  onClick={closeMobileMenu}
                >
                  Compatibility Checker
                </Link>
              </li>
              <li>
                <Link 
                  to="/regional-data" 
                  className={isActive('/regional-data') ? 'active' : ''} 
                  onClick={closeMobileMenu}
                >
                  Regional Data
                </Link>
              </li>
            </ul>
          </nav>
          
          <div className="auth-nav">
            {user ? (
              <div className="user-menu">
                <Link 
                  to="/dashboard" 
                  className={`dashboard-btn ${isActive('/dashboard') ? 'active' : ''}`}
                  onClick={closeMobileMenu}
                >
                  Dashboard
                </Link>
                <button onClick={handleSignOut} className="logout-btn">
                  Logout
                </button>
              </div>
            ) : (
              <div className="auth-buttons">
                <Link 
                  to="/login" 
                  className={`login-btn ${isActive('/login') ? 'active' : ''}`}
                  onClick={closeMobileMenu}
                >
                  Login
                </Link>
                <Link 
                  to="/register" 
                  className={`register-btn ${isActive('/register') ? 'active' : ''}`}
                  onClick={closeMobileMenu}
                >
                  Register
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
